mapper.select:
select id , article_id , title , reply_user_id , reply_user_nickname , reply_user_avatar ,
       gmt_create , content
from info_reply_me
where user_id = #{userId}
order by gmt_create desc
limit #{current} , #{limit}

mapper.update:
update info_reply_me set is_read = 1 where id in

    #{id}

and is_read = 0
